$(document).ready(function(){$(".open-modal").click(function(n){n.preventDefault();var t=$(this).attr("href");$.get(t,function(n){$("#customerDetails").html(n);$("#CustomerModal").modal("show")})});$("#export-btn").click(function(n){n.preventDefault();window.location.href='@Url.Action("Create", "Customer", new { downloadCSV = true })'});$(document).on("click","#resetButton",function(){document.cookie.split(";").forEach(n=>{document.cookie=n.replace(/^ +/,"").replace(/=.*/,"=;expires="+(new Date).toUTCString()+";path=/")});document.getElementById("searchTextbox").value="";window.location.href="search"});$(document).on("click",".delete-btn",function(n){n.preventDefault();var t=$(this).data("id");console.log("customer ID :"+t);Swal.fire({title:"Are you sure?",text:"You will not be able to recover this record!",icon:"warning",showCancelButton:!0,confirmButtonColor:"#3085d6",cancelButtonColor:"#d33",confirmButtonText:"Yes, delete it!"}).then(n=>{n.isConfirmed&&(console.log(n),$.ajax({url:"/Customer/Delete",type:"POST",data:{id:t},success:function(){$("#"+t).remove()},error:function(n){console.log(n.responseText)}}))})});$(".select-action").change(function(){var n=$(this).data("id"),t=$(this).val();$.ajax({url:"/Account/UpdateRole",type:"POST",data:{id:n,role:t},success:function(){Swal.fire({icon:"success",title:"Role changed successfully!",showConfirmButton:!1,timer:1800})},error:function(n,t,i){console.log(i)}})});$(document).on("click",".Delete-Btn",function(n){n.preventDefault();var t=$(this).data("id");console.log("customer ID :"+t);Swal.fire({title:"Are you sure?",text:"You will not be able to recover this record!",icon:"warning",showCancelButton:!0,confirmButtonColor:"#3085d6",cancelButtonColor:"#d33",confirmButtonText:"Yes, delete it!"}).then(n=>{n.isConfirmed&&(console.log(n),$.ajax({url:"/Account/DeleteRole",type:"POST",data:{id:t},success:function(){$("#"+t).remove()},error:function(n){console.log(n.responseText)}}))})})});